script:
var userId = request.getParameter('id');
var sql = 'SELECT * FROM users WHERE id = ?';
var user = db.selectOne(sql, [userId]);
return user;

var orderData = request.getParameter('data');
var sql = 'INSERT INTO orders (user_id, amount, status) VALUES (?, ?, ?)';
var orderId = db.insert(sql, [orderData.user_id, orderData.amount, 'pending']);
return {order_id: orderId, status: 'created'};

var page = request.getParameter('page').asInt(1);
var size = request.getParameter('size').asInt(10);
var offset = (page - 1) * size;
var sql = 'SELECT * FROM products LIMIT ? OFFSET ?';
var products = db.select(sql, [size, offset]);
return products;
